
class Solution:
	def threeSum(self, S):	
		n = len(S)
		if n < 3:
			return []
		elif n == 3 and sum(S) == 0:
			return [S]

		ret = []
		S.sort()

		if S[n - 1] == S[0]:
			if S[0] == 0:
				return [[0,0,0]]
			else:
				return []

		for i in range(0, n - 2):
			a = S[i]
			start = i+1
			end = n-1
			while start < end:
				b = S[start]
				c = S[end]
				if a+b+c == 0:
					ret.append((a, b, c))
					if S[start] == S[start + 1]:
						while start + 1 < n and S[start] == S[start + 1]:
							start = start + 1
					else:
						end = end - 1
				elif a+b+c > 0:
					end = end - 1
				else:
					start = start + 1
		return list(set(tuple(ret)))

sol = Solution()
print(sol.threeSum([0,-1,1,1,2,2,-2,-3,5,8]))
